create table Activity (id int, name_eng varchar(60), name_cn nvarchar(50), address varchar(100), openDate varchar(15), openTime varchar(20), telephone varchar(30), website varchar(50), price_eng varchar(15), price_cn nvarchar(30), language varchar(20), point float, rank int, attention_eng varchar(500), attention_cn nvarchar(500), intro_eng varchar(500), intro_cn nvarchar(500), view int, adore int, cityid int, countryid int, thumb_link varchar(300), img_link varchar(300), video_link varchar(300), music_link varchar(300), ad_cost float, primary key(id)) ENGINE=InnoDB DEFAULT CHARSET=utf8;

