create table City (id int, name_eng varchar(50), name_cn nvarchar(30), text_eng varchar(150), text_cn nvarchar(70), adore int, countryid int, thumb_link varchar(300), rep_link varchar(300), img_link varchar(300), video_link varchar(300), music_link varchar(300), primary key(id)) ENGINE=InnoDB DEFAULT CHARSET=utf8;

