create table Hotel (id int, name_eng varchar(50), name_cn nvarchar(30), address varchar(100), telephone varchar(30), website varchar(50), starRating int, numOfRoom int, type varchar(10), point float, rank int, intro_eng varchar(500), intro_cn nvarchar(500), view int, adore int, cityid int, countryid int, thumb_link varchar(300), img_link varchar(300), video_link varchar(300), music_link varchar(300), ad_cost float, primary key(id)) ENGINE=InnoDB DEFAULT CHARSET=utf8;

