create table Sight (id int, name_eng varchar(50), name_cn nvarchar(50), name_local nvarchar(50), address varchar(100), arrivalMode nvarchar(60), openDate varchar(15), openTime varchar(20), knownFor_eng varchar(200), knownFor_cn nvarchar(200), ticket_eng varchar(50), ticket_cn nvarchar(50), telephone varchar(30), website varchar(50), rank int, point float, intro_eng varchar(800), intro_cn nvarchar(500), view int, adore int, cityid int, countryid int, thumb_link varchar(300), img_link varchar(300), video_link varchar(300), music_link varchar(300), ad_cost int, primary key(id)) ENGINE=InnoDB DEFAULT CHARSET=utf8;

